import { useContext, useEffect } from "react";
import PopupWithForm from "../PopupWithForm/PopupWithForm.jsx";
import CurrentUserContext from "../../contexts/CurrentUserContext.js";
import useFormValidation from "../../utils/useFormValidation.js";

function EditProfilePopup({open, onClose}) {
    const currentUser = useContext(CurrentUserContext)
    const {handleChange} = useFormValidation()

    // useEffect(() => {
    //     setValue("name", currentUser.name)
    //     setValue("about", currentUser.about)
    // },[currentUser])

   // }

  return (
    <PopupWithForm 
      name="popup_type_edit"
      title="Редактировать профиль"
      formButton="Сохранить"
      open={open}
      onClose={onClose}
    >
      <input
        required=""
        id="input-name"
        className="form__subtitle form__subtitle_text_name"
        type="text"
        name="name"
        placeholder="Имя"
        minLength={2}
        maxLength={40}
      />
      <span id="input-name-error" className="error-message">
        Вы пропустили это поле.
      </span>
      <input
        required=""
        id="input-occupation"
        className="form__subtitle form__subtitle_text_job"
        type="text"
        name="about"
        placeholder="Занятие"
        minLength={2}
        maxLength={200}
      />
      <span id="input-occupation-error" className="error-message">
        Вы пропустили это поле.
      </span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
