import { useCallback, useState } from "react";

function useFormValidation () {
    const [values, setValues] = useState({})
    const [errors, setError] = useState ({})
    const [isValid, setIsValid] = useState(false)
    const [isInputValid, setIsInputValid] = useState({})



    function handleChange (event) {
        const name = evt.target.name
        const value = evt.target.value
        const validationMessage = evt.target.validationMessage
        const valid = evt.target.validity.valid
        const form = evt.target.form  //воспринимает как 2 разных инпута = 2 разные форма - откладываю

        setIsInputValid((oldIsInputValid) => {
            return { ...oldIsInputValid, [name]: valid}
        })

        setValues((oldValues) => {
            return { ...oldValues, [name]: value}
        })

        setError((oldErrors) => {
            return { ...oldErrors, [name]: validationMessage}
        })

        setIsValid(form.checkValidity())
    }

 function reset (data = {}) {
    setValues (data)
    setError ({})
    setIsValid (false)
    setIsInputValid ({})
 }

 const setValue = useCallback ((name, value) => {
    setValues((oldValues) => {
        return { ...oldValues, [name]: value}
    })
 }, [])

    return { values, errors, isValid, isInputValid, handleChange, reset, setValue }

}

export default useFormValidation
